public class ElaborateSwitch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int i=10;

switch(i){
case 1: System.out.println("No is 1");

case 2: System.out.println("Number is 2");


case 10: System.out.println("Number is 10");

case 11: System.out.println("Didn't used the break");
}
	}

}
