package logicalProgram;

import java.util.Scanner;

public class KeywordEntry {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		System.out.println("Enter the number you want to Enter");
		int x=(int) sc.nextFloat();
		System.out.println("Number you entered is:"+x);
		
		Scanner s=new Scanner (System.in);
		
		System.out.println("Hi..... Which number you want to Enter");
		int p=s.nextInt();
		System.out.println("Ohhh... You have entered "+p);
	}

}
