public class Pattern2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i,j;
		int num=5;
		for(i=0; i<num; i++){
			
			for(j=i; j<num; j++){
				System.out.print("*");
			}
			System.out.println();
		}
	}

}
